--Crear tablas
CREATE TABLE producto (id INTEGER NOT NULL, nombre VARCHAR (45), descripcion VARCHAR (45), estado VARCHAR (3),  PRIMARY KEY (ID)); 
CREATE TABLE kardex (id INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 100, INCREMENT BY 1) NOT NULL, fecha TIMESTAMP, id_catologo INTEGER NOT NULL, detalle VARCHAR (45), ent_catidad INTEGER, ent_valor_unitario DECIMAL(10,2), ent_valor_total DECIMAL(10,2), sal_catidad INTEGER, sal_valor_unitario DECIMAL(10,2), sal_valor_total DECIMAL(10,2), exi_catidad INTEGER, exi_valor_unitario DECIMAL(10,2), exi_valor_total DECIMAL(10,2), stock_minimo INTEGER, PRIMARY KEY (ID));
CREATE TABLE persona (id INTEGER NOT NULL, identificacion VARCHAR(15), nombre VARCHAR(45), ciudad VARCHAR(45), correo_electronico VARCHAR(45), direccion VARCHAR(45), telefono VARCHAR(45), tipo_persona VARCHAR(3), username VARCHAR(45), password VARCHAR(256), estado VARCHAR(3),  PRIMARY KEY (ID));
